import React from 'react'
import Header from '../components/Layout/Header'
import { TextField } from '@mui/material'
import Form from '../components/Form/Form'

const Items = () => {
  return (
    <div>
      <Header />

      <section>
        <div style={{display: 'inline-block', marginTop: '20px'}}>
          <h2 className='ml-6 text-3xl text-red-700 font-bold font-sans inline-block shadow-black shadow-lg'>MANAGE ITEMS</h2>
        </div>

        <div className='absolute top-0 bottom-0 left-0 right-0 m-auto ms-auto shadow-lg shadow-black h-72 w-4/5 rounded-lg'>
          <h3 className='text-xl text-blue-800 font-serif font-bold text-center mt-3'>Save | Update | Delete Item</h3>

          <div className='absolute top-0 bottom-0 left-0 right-0 m-auto ms-auto w-11/12 h-2/4 inline-block space-x-6'>
          {/* <TextField id="outlined-text-input" label="Item Id" type="text" />
          <TextField id="outlined-text-input" label="Description" type="text" className='text-white' style={{width:'400px'}}/>
          <TextField id="outlined-text-input" label="Unit Price" placeholder='Rs.0.00' type="" className='text-white'/>
          <TextField id="outlined-text-input" label="Item Name" type="text" className='text-white'/>
          <TextField id="outlined-text-input" label="Item Name" type="text" className='text-white'/> */}

        <Form
          textFieldsArray={[
            {
              label: "Customer ID",
              textFieldType: "text",
              name: "customerId",
              placeHolderText: "Customer ID",
              // value: customerID,
              // onChange: (event) => {
              //   setCustomerID(event.target.value);
              // },
            },
            {
              label: "Username",
              textFieldType: "text",
              name: "username",
              placeHolderText: "Username",
              // value: username,
              // onChange: (event) => {
              //   setUsername(event.target.value);
              // },
            },
            {
              label: "Password",
              textFieldType: "text",
              name: "password",
              placeHolderText: "Password",
              // value: password,
              // onChange: (event: ChangeEvent<HTMLInputElement>) => {
              //   setPassword(event.target.value);
              // },
            },
            {
              label: "Name",
              textFieldType: "text",
              name: "name",
              placeHolderText: "Name",
              // value: customerName,
              // onChange: (event: ChangeEvent<HTMLInputElement>) => {
              //   setCustomerName(event.target.value);
              // },
            },
            {
              label: "Address",
              textFieldType: "text",
              name: "address",
              placeHolderText: "Address",
              // value: address,
              // onChange: (event: ChangeEvent<HTMLInputElement>) => {
              //   setAddress(event.target.value);
              // },
            },
            {
              label: "Contact Number",
              textFieldType: "text",
              name: "contactNumber",
              placeHolderText: "Contact Number",
              // value: contactNumber,
              // onChange: (event: ChangeEvent<HTMLInputElement>) => {
              //   setContactNumber(event.target.value);
              // },
            },
            {
              label: "Email",
              textFieldType: "text",
              name: "email",
              placeHolderText: "Email",
              // value: email,
              // onChange: (event: ChangeEvent<HTMLInputElement>) => {
              //   setEmail(event.target.value);
              // },
            },
          ]}
          buttonsArray={[
            {
              color: "success",
              icon: <AddCircleIcon />,
              text: "Save",
              // onClick: handleSaveCustomer,
            },
            {
              color: "primary",
              icon: <AutorenewIcon />,
              text: "Update",
              // onClick: handleUpdateCustomer,
            },
            {
              color: "error",
              icon: <DeleteIcon />,
              text: "Delete",
              // onClick: handleDeleteCustomer,
            },
            {
              color: "warning",
              icon: <BackspaceIcon />,
              text: "Clear",
              // onClick: handleClearFields,
            },
          ]}
        />

          </div>        
          {/* <div className=''></div> */}
        </div>

      </section>
      
    </div>
  )
}

export default Items
