import { Button } from "@mui/material";
import React, { MouseEventHandler, useEffect } from "react";

// type TableProps = {
//   tblName: string;
//   tblHeight: string;
//   tblHeaders: string[];
//   tblData: Array<string[] | any[]>;
//   handleTblRowClick?: (tableRow: Array<String | any>) => void;
// };

const Table = (props) => {

  useEffect(() => {
    console.log("Table length : "+props.tblHeaders.length);
    console.log(tblHeaderStyles);
  })

  const handleRowClick = (tableRow ) => {
    const textValue = tableRow;
    // console.log(textValue);
    if (props.handleTblRowClick) {
      props.handleTblRowClick(tableRow);
    }
  };


  // const tblHeaderStyles = `grid grid-cols-5 h-full`;
  const tblHeaderStyles = {
    display: "grid",
    gridTemplateColumns: `repeat(${props.tblHeaders.length}, minmax(0, 1fr))`,
  };

  // // let tblDataRow = `grid grid-cols-${tblHeaders.length} cursor-pointer`;
  let tblDataRow = {
    display: "grid",
    gridTemplateColumns: `repeat(${props.tblHeaders.length}, minmax(0, 1fr))`,
    minHeight: "60px",
  };

  // // let tableHeight = `!h-[${tblHeight}]`;
  const tableHeight = {
    height: `${props.tblHeight}`,
  };

  return (
    <section id="tblContainer">
      <section
        className="flex flex-col h-50 shadow-lg items-center
        pt-3 px-3"
      >
        <h3
          className="text-center bg-[#0D6EFC] p-2 text-[rgb(248,249,250)] mb-2 rounded-[8px] w-full text-[24px]"
          style={{ fontWeight: "500", lineHeight: "28.8px" }}
        >
          {/* Your Items Cart */}
          {props.tblName}
        </h3>
        <div className="w-full pb-6 rounded-2 overflow-x-auto scroll-smooth">
          <Table className="table-fixed border-collapse grid grid-cols-1">
            <thead
              className="text-white grid grid-cols-1 h-[43px]"
              style={{
                background: "#17377f",
                borderTopLeftRadius: "8px",
                borderTopRightRadius: "8px",
              }}
            >
              <tr>
                {props.tblHeaders.map((header, index) => (
                  <th
                    key={index}
                    className="h-full border border-slate-300 flex justify-center items-center"
                  >
                    {header}
                  </th>
                ))}
          
              </tr>
            </thead>

            <tbody style={props.tableHeight} className="cursor-pointer">
              {props.tblData.map((dataRow, index) => (
                <tr key={index} style={tblDataRow}>
                  {dataRow.map((data, index) => (
                    <td
                      key={index}
                      className="border border-slate-300 p-2 flex items-center justify-center"
                    >
                      {data === "Cancel" ? (
                        <Button
                          className="!px-[10px] !capitalize !font-poppins !font-normal !text-[15px]"
                          variant="contained"
                          color="error"
                        >
                          {data}
                        </Button>
                      ) : data == "Deny" ? (
                        <Button
                          className="!px-[10px] !capitalize !font-poppins !font-normal !text-[15px]"
                          variant="contained"
                          color="error"
                        >
                          {data}
                        </Button>
                      ) : data == "Accept" ? (
                        <Button
                          className="!px-[10px] !capitalize !font-poppins !font-normal !text-[15px]"
                          variant="contained"
                          color="success"
                        >
                          {data}
                        </Button>
                      ) : (
                        data
                      )}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </section>
    </section>
  );
};

export default Table;
